      e.preventDefault();
      if (!draggedItem) return;

      const isCorrect = draggedItem.correct === binId;
      const newScore = isCorrect ? score + draggedItem.points : Math.max(0, score - 5);
      
      setScore(newScore);
      setDroppedItems(prev => ({
