      if (isCorrect) {
        setFeedback(`âœ… Correct! +${draggedItem.points} points`);
      } else {
        setFeedback(`âŒ Incorrect. ${draggedItem.name} belongs in ${bins.find(b => b.id === draggedItem.correct)?.name}`);
      }

      setTimeout(() => setFeedback(''), 2000);
      setDraggedItem(null);

      // Check if game is complete
      const totalDropped = Object.values(droppedItems).flat().length + 1;
      if (totalDropped >= wasteItems.length) {
        setTimeout(() => onComplete(newScore), 1000);
